const WEBPACK_URL_REGEX = /webpack-internal:\/\/(.+?[^/]\/)([^?]+)(.*)$/

function parseWebpackSourceMapUrl(url: string) {
	const matches = WEBPACK_URL_REGEX.exec(url)
	if (matches && matches.length > 2) {
		const module = matches[1]
		const filePath = matches[2]
		return {
			module,
			filePath
		}
